import React from 'react';
import './Signup.css'


class SignUp extends React.Component {
  constructor(props) {
  		super(props);
  		this.state = {
  			fname: "",
  			lname: "",
  			email: "",
        password: ""
  		}
  }

  // defining a method (arrow notation method)
  // which automatically binds to 'this'
  signUp = () => {
    fetch("http://localhost:5000/user/signup", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(this.state),
    })
      .then(this.fetchCheckStatus)
      .then(response => response.json())
      .then(data => {
          // this data can contain a json that says
          // the user is signed up successfully (email dne already)
          // all fields must be entered

          console.log(data);
      })
      .catch(error => {
        window.alert(error);
        return;
      });
  };
  fetchCheckStatus = (response) => {
  	if (response.status >= 200 && response.status < 300) {
  		return Promise.resolve(response)
  	} else {
  		response.status = "Transaction rejected ...";
  		return Promise.reject(new Error(response))
  	}
  }
  render() {
    return (
      <div>
        <h3>Sign Up</h3>
        <form onSubmit={this.signUp}>
          <div className="form-group">
            <label htmlFor="fname">First Name</label>
            <input
              type="text"
              className="form-control"
              id="fname"
              value={this.state.fname}
              onChange={(e) => this.setState({ fname: e.target.value })}
            />
          </div>
          <div className="form-group">
            <label htmlFor="lname">Last Name</label>
            <input
              type="text"
              className="form-control"
              id="lname"
              value={this.state.lname}
              onChange={(e) => this.setState({ lname: e.target.value })}
            />
          </div>
          <div className="form-group">
            <label htmlFor="email">E-mail</label>
            <input
              type="text"
              className="form-control"
              id="email"
              value={this.state.email}
              onChange={(e) => this.setState({ email: e.target.value })}
            />
          </div>
          <div className="form-group">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              className="form-control"
              id="password"
              value={this.state.password}
              onChange={(e) => this.setState({ password: e.target.value })}
            />
          </div>
          <div className="form-group">
            <input
              type="submit"
              value="Sign Up"
              className="btn btn-primary"
            />
          </div>
        </form>
      </div>
    );
  }
}
export default SignUp;
